"use strict";

var slice = require('./slice');

/**
 * Returns all but the first element of the given array.
 *
 * @param {Array} arr
 * @return {*}
 * @example
 *
 *      tail(['fi', 'fo', 'fum']); //=> ['fo', 'fum']
 *      tail([]); //=> []
 */
module.exports = function (arr) {
  return slice(1, arr.length, arr);
};