"use strict";

var isNumber = require('./number');

/**
 * Checks if `test` is finite.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is finite, else `false`.
 * @example
 *
 * isFinite(5); // => true
 * isFinite(null); // => false
 */
module.exports = function (test) {
  return isNumber(test) && isFinite(test);
};