"use strict";

/**
 * Checks if `test` is function.
 *
 * @param {*} test The value to check.
 * @returns {boolean} Returns `true` if `test` is function, else `false`.
 * @example
 *
 * isFunction(() => {}); // => true
 * isFunction(null); // => false
 */
module.exports = function (test) {
  return typeof test === 'function';
};