"use strict";

/**
 * Removes leading whitespace from `str`.
 *
 * @param {String} str The string to trim.
 * @returns {String} Returns the trimmed string.
 * @example
 *
 * trimLeft('  abc  '); // => 'abc   '
 *
 */
module.exports = function (str) {
  if (str === void 0) {
    str = '';
  }

  return str.replace(/^[\s\uFEFF\xA0]+/g, '');
};